import React, {
  ReactElement,
  createContext,
  useContext,
  useEffect,
  useState,
} from "react";
import { initDeck } from "../utils/Deck";

interface IGameContext {
  deck: number[];
}

const initialContext = {
  deck: [],
};

const GameContext = createContext<IGameContext>(initialContext);

export const useGameDetails = () => {
  const contextValue = useContext(GameContext);

  if (!contextValue) {
    throw new Error("useGameDetails must be called within GameContextProvider");
  }
  return contextValue;
};

export const GameContextProvider = (children: ReactElement[]) => {
  const [deck, setDeck] = useState<number[]>([]);

  useEffect(() => {
    setDeck(initDeck());
  }, []);

  const value = {
    deck,
  };

  return <GameContext.Provider value={value}>{children}</GameContext.Provider>;
};
