import { useEffect, useState } from "react";

const useTimer = () => {
  const [countdown, setCountdown] = useState(60000);

  useEffect(() => {
    const interval = setInterval(() => {
      setCountdown(countdown - 1000);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return { countdown };
};

export default useTimer;
