// import Landing from "../Landing/Landing";
import { Modal } from "../Modal/Modal";
import { ScoreType } from "../../types/ScoreType";
import { useGameDetails } from "../../context/GameLogic";

interface IProps {
  playing: boolean;
  onEnter: (inputName: string) => void;
  showRulesComponent: () => void;
  inputName: string;
  isHighscore: (score: ScoreType) => boolean;
}

export const EndGameModal = ({
  playing,
  onEnter,
  showRulesComponent,
  inputName,
  isHighscore,
}: IProps) => {
  const { score } = useGameDetails();

  if (!playing) isHighscore({ name: inputName, score: score });

  // console.log("score", score, isHighscore({ name: inputName, score: score }));

  if (isHighscore({ name: inputName, score: score })) {
    return (
      <Modal>
        <div className="landing">
          <h1 id={"title"}>You got a high score of {score} sets!</h1>
          <button id="enter-button" onClick={() => onEnter(inputName)}>
            Play again!
          </button>
          <button id="rules-button" onClick={() => showRulesComponent()}>
            Want to take another look at the rules? Click HERE
          </button>
        </div>
      </Modal>
    );
  } else {
    return (
      <Modal>
        <div className="landing">
          <h1 id={"title"}>Time's up! You found {score} Sets!</h1>
          <button id="enter-button" onClick={() => onEnter(inputName)}>
            Play again!
          </button>
          <button id="rules-button" onClick={() => showRulesComponent()}>
            Want to take another look at the rules? Click HERE
          </button>
        </div>
      </Modal>
    );
  }
};
