import "./Landing.css";
import { useState, useEffect } from "react";

const Landing = ({ onEnter, showRulesComponent }: any) => {
  const [inputName, setInputName] = useState("");

  const handleKeyDown = (e: KeyboardEvent) => {
    const enterButton = document.getElementById("enter");
    if (e.key === "Enter") {
      enterButton?.click();
    }
  };

  //if enter is pressed, click on the enter button
  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  //useEffect to focus on the input field on first load
  useEffect(() => {
    const input = document.getElementById("name-input");
    input?.focus();
  }, []);

  return (
    <div className="landing">
      <h2>Welcome to the game of Set!</h2>
      <h3>Enter your name to start</h3>
      <input
        type="text"
        value={inputName}
        id="name-input"
        onChange={(e) => setInputName(e.target.value)}
      />
      <button id="enter" onClick={() => onEnter(inputName)}>
        ENTER
      </button>
      <button onClick={() => showRulesComponent()}>
        First time playing? Check out the rules HERE
      </button>
    </div>
  );
};

export default Landing;
