import { createContext, useContext, useState, useEffect } from "react";
import { initDeck, checkSet, updateDeck } from "../utils/utils";
import { Card } from "../types/Card";

interface IGameContext {
  deck: Card[];
  setDeck: (deck: Card[]) => void;
}

const initialContext = {
  deck: [],
  setDeck: () => {},
};

const GameContext = createContext<IGameContext>(initialContext);

// Hook that returns the Context value
export const useGameDetails = () => {
  const contextValue = useContext(GameContext);

  if (!contextValue) {
    throw new Error("useGameDetails must be called within GameContextProvider");
  }
  return contextValue;
};

// Context provider that returns the context that wraps the children to give them access to the state
export const GameContextProvider = ({ children }: any) => {
  const [deck, setDeck] = useState<Card[]>(initDeck());
  const [score, setScore] = useState<number>(0);

  const value = {
    deck,
    setDeck,
  };

  useEffect(() => {
    const selectedCards = deck.filter((card) => card.selected === true);
    if (selectedCards.length === 3) {
      if (checkSet(selectedCards)) {
        // let newDeck = [...deck];
        // newDeck.forEach((card) => (card.set = true));
        // setDeck(newDeck);
        const newScore = score + 1;
        setScore(newScore);

        setTimeout(() => {
          setDeck(updateDeck(deck));
        }, 1000);
      } else {
        // There's not a set in the selected cards
        // Set border to red and display for 1 sec
        // Then remove border
        /**
         * Find the 3 selected cards in the deck and set a property that
         * indicates that they are not a SET so then we can render the appropiate class
         */
        let newDeck = [...deck];
        newDeck.forEach((card) => {
          card.selected = false;
        });
        setDeck(newDeck);
      }
      // resetSelectedCards(deck);
    }
  }, [deck]);

  // const resetSelectedCards = (deck: Card[]) => {
  //   setTimeout(() => {
  //     let newDeck = [...deck];
  //     newDeck.forEach((card) => {
  //       card.selected = false;
  //     });
  //     setDeck(newDeck);
  //   }, 1000);
  // };

  return <GameContext.Provider value={value}>{children}</GameContext.Provider>;
};
