import { createPortal } from "react-dom";
import "./Rules.css";

const Rules = ({ closeRulesModal }: any) => {
  const exampleUrl1 = require(`../../public/images/cards/${card.id}.png`);
  const exampleUrl2 = require(`../../public/images/cards/${card.id}.gif`);
  const exampleUrl3 = require(`../../public/images/cards/${card.id}.gif`);
  return createPortal(
    <div className="rules">
      <h1>Rules and How to Play</h1>
      <h2>
        The purpose of the Game is to identify SETs of 3 from the 12 cards
        displayed in the board.
      </h2>

      <h2>What is a Set?</h2>
      <h3>
        A set consists of three cards in which each feature (shape, color,
        number and shading) are all the same, or all different.
      </h3>

      <h2>Examples of Sets:</h2>
      <div className="images-container">
        <img src="../../example1" alt="example1"></img>
        <img src="example2" alt="example2"></img>
        <img src="example3" alt="example3"></img>
      </div>
      <h2>How to Play:</h2>
      <h3>
        Once you think youâ€™ve found a set, click on the cards. If it is a set,
        the selected cards will turn green, a +1 will be added to your score,
        and the three cards will be replaced by new cards. If it is not a set,
        the cards will turn red and your score will decrease be 1.
      </h3>
      <button onClick={() => closeRulesModal()}>CLOSE</button>
    </div>,
    document.body
  );
};

export default Rules;
