import { checkSet } from "../utils/Deck";
import styles from "./Gameboard.module.css";
import React, { useEffect, useState } from "react";

interface GameboardProps {
  deck: number[];
  setDeck: (deck: number[], selectxedCards: number[]) => void;
}

export default function Gameboard({ deck, setDeck }: GameboardProps) {
  const [selectedCards, setSelectedCards] = useState<number[]>([]);

  const selectCard = (card: number) => {
    if (selectedCards.length < 3 && !selectedCards.includes(card)) {
      const newSelectedCards = [...selectedCards, card];
      setSelectedCards(newSelectedCards);
    }
  };

  useEffect(() => {
    if (selectedCards.length === 3) {
      if (checkSet(selectedCards)) {
        setDeck(deck, selectedCards);
        setSelectedCards([]);
      }
    }
  }, [selectedCards]);

  return (
    <div className={styles.gameboard}>
      {deck.length &&
        deck.map((card) => {
          const imageUrl = require(`../../public/images/cards/${card}.gif`);
          return (
            <img
              className={`${styles.card} ${
                selectedCards.includes(card) ? styles.selectedCard : ""
              }`}
              src={imageUrl}
              width={258}
              height={167}
              alt="card"
              key={card}
              onClick={() => selectCard(card)}
            />
          );
        })}
    </div>
  );
}
